INSERT INTO `wp_posts` VALUES (4099,5,'2013-02-20 15:01:09','2013-02-20 15:01:09','Steve is CEO Partner at Hi Tech Partners and CEO of AuditMyBooks, a SaaS supplier of FinTech solutions. Steve has taken multiple companies from idea, to first sales, to high growth, to commercial sale (McAfee, NetIQ, and CA) with no funding, angel funding and venture funding. His expertise is in software, financial technologies and mobile. Steve’s strength is startup sales and operations with insights on doing more with less.','Steve Bachman','','publish','closed','closed','','steve-bachman','','','2013-02-20 15:01:09','2013-02-20 15:01:09','',0,'http://flashpoint.ei2.org/?post_type=mentors&#038;p=4099',0,'mentors','',0);
